import {React ,useState} from 'react'


const Toast = ({ message, onClose }) => {
    return (
      <div className="max-w-xs bg-gray-800 text-sm text-white rounded-xl shadow-lg dark:bg-gray-900" role="alert">
        <div className="flex p-4">
          {message}
          <div className="ms-auto">
            <button
              type="button"
              className="inline-flex flex-shrink-0 justify-center items-center h-5 w-5 rounded-lg text-white hover:text-white opacity-50 hover:opacity-100 focus:outline-none focus:opacity-100"
              onClick={onClose}
            >
              <span className="sr-only">Close</span>
              <svg
                className="flex-shrink-0 w-4 h-4"
                xmlns="http://www.w3.org/2000/svg"
                width="24"
                height="24"
                viewBox="0 0 24 24"
                fill="none"
                stroke="currentColor"
                strokeWidth="2"
                strokeLinecap="round"
                strokeLinejoin="round"
              >
                <path d="M18 6 6 18" />
                <path d="m6 6 12 12" />
              </svg>
            </button>
          </div>
        </div>
      </div>
    );
  };

  const ToastComponent = () => {
    
    const [showToast, setShowToast] = useState(false);
  
    const handleShowToast = () => {
      setShowToast(true);
      // You can set a timeout here to hide the toast after a certain duration
      // setTimeout(() => setShowToast(false), 5000);
    };
  
    const handleCloseToast = () => {
      setShowToast(false);
    };
  
    return (
      <div>
        <button onClick={handleShowToast}>Show Toast</button>
        {showToast && <Toast message="Hello, world! This is a toast message." onClose={handleCloseToast} />}
      </div>
    );
  };
  
  export default ToastComponent;
